import React, { useState, useEffect } from 'react';
import { Box, Container, Typography, TextField, Paper, Fab } from '@mui/material';
import { styled } from '@mui/material/styles';
import { motion } from 'framer-motion';
import ChatIcon from '@mui/icons-material/Chat';
import { useNavigate } from 'react-router-dom';

const SearchContainer = styled(Box)(({ theme }) => ({
  height: '100vh',
  width: '100%',
  display: 'flex',
  alignItems: 'center',
  justifyContent: 'center',
  background: 'linear-gradient(135deg, #f5f7fa 0%, #c3cfe2 100%)',
  position: 'relative',
  marginTop: -theme.spacing(8), // Compensate for header height
}));

const SearchBox = styled(Paper)(({ theme }) => ({
  padding: theme.spacing(4),
  width: '100%',
  maxWidth: 600,
  borderRadius: theme.spacing(2),
  boxShadow: '0 8px 32px rgba(0,0,0,0.1)',
  background: 'rgba(255,255,255,0.95)',
  backdropFilter: 'blur(10px)',
  '&.blink-glow': {
    animation: 'glowAnimation 2s ease-in-out',
  },
  '@keyframes glowAnimation': {
    '0%': {
      boxShadow: '0 8px 32px rgba(0,0,0,0.1)',
    },
    '50%': {
      boxShadow: '0 8px 32px rgba(25, 118, 210, 0.5)',
    },
    '100%': {
      boxShadow: '0 8px 32px rgba(0,0,0,0.1)',
    },
  },
}));

const StyledTextField = styled(TextField)(({ theme }) => ({
  '& .MuiOutlinedInput-root': {
    borderRadius: theme.spacing(2),
    transition: 'all 0.3s ease-in-out',
    '&:hover': {
      transform: 'scale(1.01)',
    },
    '&.Mui-focused': {
      transform: 'scale(1.02)',
    },
  },
}));

const ChatbotButton = styled(Fab)(({ theme }) => ({
  position: 'fixed',
  bottom: theme.spacing(4),
  right: theme.spacing(4),
  zIndex: 1000,
}));

const Home = () => {
  const [searchValue, setSearchValue] = useState('');
  const [isGlowing, setIsGlowing] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    // Check if we should trigger the glow effect (based on URL parameter)
    const params = new URLSearchParams(window.location.search);
    if (params.get('glow') === 'true') {
      setIsGlowing(true);
      // Remove the parameter after triggering the effect
      window.history.replaceState({}, '', window.location.pathname);
      // Reset the glow effect after animation
      setTimeout(() => setIsGlowing(false), 2000);
    }
  }, []);

  return (
    <SearchContainer>
      <Container maxWidth="md">
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
          style={{ width: '100%' }}
        >
          <SearchBox id="search-section" className={isGlowing ? 'blink-glow' : ''}>
            <Typography
              variant="h3"
              component="h1"
              align="center"
              gutterBottom
              sx={{ fontWeight: 'bold', color: 'primary.main' }}
            >
              What can I help with?
            </Typography>
            <motion.div
              whileHover={{ scale: 1.02 }}
              transition={{ type: 'spring', stiffness: 300 }}
            >
              <StyledTextField
                fullWidth
                variant="outlined"
                placeholder="Search for legal judgments, cases, or topics..."
                value={searchValue}
                onChange={(e) => setSearchValue(e.target.value)}
                sx={{ mt: 4 }}
                InputProps={{
                  sx: {
                    fontSize: '1.1rem',
                    padding: '12px',
                  },
                }}
              />
            </motion.div>
          </SearchBox>
        </motion.div>
      </Container>
      <ChatbotButton
        color="primary"
        onClick={() => navigate('/chatbot')}
        aria-label="Open Chatbot"
      >
        <ChatIcon />
      </ChatbotButton>
    </SearchContainer>
  );
};

export default Home;
