import React, { useState, useEffect } from 'react';
import { Box, Typography, TextField, Paper, Fab, Grid, Card, CardContent, CardMedia, IconButton, Divider } from '@mui/material';
import { styled } from '@mui/material/styles';
import { motion } from 'framer-motion';
import ChatIcon from '@mui/icons-material/Chat';
import LinkedInIcon from '@mui/icons-material/LinkedIn';
import GitHubIcon from '@mui/icons-material/GitHub';
import EmailIcon from '@mui/icons-material/Email';
import LocationOnIcon from '@mui/icons-material/LocationOn';
import PhoneIcon from '@mui/icons-material/Phone';
import AccessTimeIcon from '@mui/icons-material/AccessTime';
import { useNavigate, useLocation } from 'react-router-dom';

const SearchContainer = styled(Box)(({ theme }) => ({
  height: '100vh', // Full viewport height
  width: '100%',
  display: 'flex',
  alignItems: 'center',
  justifyContent: 'center',
  background: 'linear-gradient(135deg, #f5f7fa 0%, #c3cfe2 100%)',
  position: 'relative',
  paddingTop: theme.spacing(8), // Account for header
}));

const NewsSection = styled(Box)(({ theme }) => ({
  minHeight: '100vh',
  width: '100%',
  background: '#ffffff',
  padding: theme.spacing(10, 2),
}));

const NewsCard = styled(Card)(({ theme }) => ({
  height: '100%',
  display: 'flex',
  flexDirection: 'column',
  transition: 'transform 0.3s ease-in-out',
  '&:hover': {
    transform: 'translateY(-8px)',
  },
}));

const SearchBox = styled(Paper)(({ theme }) => ({
  padding: theme.spacing(6),
  width: '100%',
  maxWidth: '80%',
  borderRadius: theme.spacing(2),
  boxShadow: '0 8px 32px rgba(0,0,0,0.1)',
  background: 'rgba(255,255,255,0.95)',
  backdropFilter: 'blur(10px)',
  margin: '0 auto',
  '&.blink-glow': {
    animation: 'glowAnimation 1.5s ease-in-out',
  },
  '@keyframes glowAnimation': {
    '0%': {
      boxShadow: '0 8px 32px rgba(0,0,0,0.1)',
    },
    '50%': {
      boxShadow: '0 8px 32px rgba(25, 118, 210, 0.8)',
    },
    '100%': {
      boxShadow: '0 8px 32px rgba(0,0,0,0.1)',
    },
  },
  [theme.breakpoints.down('sm')]: {
    maxWidth: '95%',
    padding: theme.spacing(4),
  },
}));

const StyledTextField = styled(TextField)(({ theme }) => ({
  '& .MuiOutlinedInput-root': {
    borderRadius: theme.spacing(2),
    transition: 'all 0.3s ease-in-out',
    fontSize: '1.2rem', // Increased font size
    '&:hover': {
      transform: 'scale(1.01)',
    },
    '&.Mui-focused': {
      transform: 'scale(1.02)',
    },
  },
  '& .MuiOutlinedInput-input': {
    padding: '16px 20px', // Increased padding
  },
}));

const ChatbotButton = styled(Fab)(({ theme }) => ({
  position: 'fixed',
  bottom: theme.spacing(4),
  right: theme.spacing(4),
  zIndex: 1000,
}));

const AboutSection = styled(Box)(({ theme }) => ({
  minHeight: '100vh',
  width: '100%',
  background: 'linear-gradient(135deg, #f5f7fa 0%, #c3cfe2 100%)',
  padding: theme.spacing(10, 2),
}));

const TeamCard = styled(Card)(({ theme }) => ({
  height: '100%',
  display: 'flex',
  flexDirection: 'column',
  transition: 'transform 0.3s ease-in-out',
  overflow: 'visible',
  background: 'rgba(255, 255, 255, 0.9)',
  '&:hover': {
    transform: 'translateY(-8px)',
  },
}));

const ContactCard = styled(Paper)(({ theme }) => ({
  padding: theme.spacing(4),
  background: 'rgba(255, 255, 255, 0.9)',
  borderRadius: theme.spacing(2),
  marginTop: theme.spacing(6),
}));

const ContactItem = styled(Box)(({ theme }) => ({
  display: 'flex',
  alignItems: 'center',
  marginBottom: theme.spacing(2),
  '& .MuiSvgIcon-root': {
    marginRight: theme.spacing(2),
    color: theme.palette.primary.main,
  },
}));

const Home = () => {
  const [searchValue, setSearchValue] = useState('');
  const [isGlowing, setIsGlowing] = useState(false);
  const navigate = useNavigate();
  const location = useLocation();

  // Mock news data
  const newsItems = [
    {
      id: 1,
      title: "Landmark Supreme Court Judgment",
      description: "Recent ruling sets new precedent in environmental law cases...",
      image: "/path-to-image-1.jpg",
      date: "March 15, 2024"
    },
    {
      id: 2,
      title: "High Court's Digital Initiative",
      description: "New online filing system revolutionizes court proceedings...",
      image: "/path-to-image-2.jpg",
      date: "March 14, 2024"
    },
    {
      id: 3,
      title: "Legal AI Breakthrough",
      description: "Artificial Intelligence making waves in legal research...",
      image: "/path-to-image-3.jpg",
      date: "March 13, 2024"
    }
  ];

  // Handle glow effect
  const handleGlowEffect = () => {
    setIsGlowing(true);
    // Remove the parameter after triggering the effect
    window.history.replaceState({}, '', window.location.pathname);
    // Reset the glow effect after animation
    setTimeout(() => setIsGlowing(false), 1500); // Match animation duration
  };

  useEffect(() => {
    // Check URL parameters when component mounts or URL changes
    const params = new URLSearchParams(window.location.search);
    if (params.get('glow') === 'true') {
      handleGlowEffect();
    }
    if (params.get('section') === 'about') {
      const aboutElement = document.getElementById('about-section');
      if (aboutElement) {
        aboutElement.scrollIntoView({ behavior: 'smooth' });
        // Remove the parameter after scrolling
        window.history.replaceState({}, '', window.location.pathname);
      }
    }
  }, [location.search]); // React to URL changes

  // Listen for popstate events (triggered by header)
  useEffect(() => {
    const handlePopState = () => {
      const params = new URLSearchParams(window.location.search);
      if (params.get('glow') === 'true') {
        handleGlowEffect();
      }
    };

    window.addEventListener('popstate', handlePopState);
    return () => window.removeEventListener('popstate', handlePopState);
  }, []);

  return (
    <>
      <SearchContainer>
        <Box width="100%" display="flex" justifyContent="center" alignItems="center">
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8 }}
            style={{ width: '100%', display: 'flex', justifyContent: 'center' }}
          >
            <SearchBox id="search-section" className={isGlowing ? 'blink-glow' : ''}>
              <Typography
                variant="h2"
                component="h1"
                align="center"
                gutterBottom
                sx={{ 
                  fontWeight: 'bold', 
                  color: 'primary.main',
                  mb: 4,
                  fontSize: { xs: '2.5rem', md: '3.5rem' },
                }}
              >
                What can I help with?
              </Typography>
              <motion.div
                whileHover={{ scale: 1.02 }}
                transition={{ type: 'spring', stiffness: 300 }}
              >
                <StyledTextField
                  fullWidth
                  variant="outlined"
                  placeholder="Search for legal judgments, cases, or topics..."
                  value={searchValue}
                  onChange={(e) => setSearchValue(e.target.value)}
                  sx={{ mt: 4 }}
                />
              </motion.div>
            </SearchBox>
          </motion.div>
        </Box>
        <ChatbotButton
          color="primary"
          onClick={() => navigate('/chatbot')}
          aria-label="Open Chatbot"
        >
          <ChatIcon />
        </ChatbotButton>
      </SearchContainer>

      <NewsSection>
        <Box maxWidth="lg" mx="auto">
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8 }}
            viewport={{ once: true }}
          >
            <Typography
              variant="h3"
              component="h2"
              align="center"
              gutterBottom
              sx={{
                fontWeight: 'bold',
                mb: 6,
                color: 'primary.main',
              }}
            >
              Popular News
            </Typography>

            <Grid container spacing={4}>
              {newsItems.map((news, index) => (
                <Grid item xs={12} md={4} key={news.id}>
                  <motion.div
                    initial={{ opacity: 0, y: 20 }}
                    whileInView={{ opacity: 1, y: 0 }}
                    transition={{ duration: 0.5, delay: index * 0.2 }}
                    viewport={{ once: true }}
                  >
                    <NewsCard>
                      <CardMedia
                        component="img"
                        height="200"
                        image={news.image}
                        alt={news.title}
                        sx={{ objectFit: 'cover' }}
                      />
                      <CardContent>
                        <Typography variant="h5" gutterBottom>
                          {news.title}
                        </Typography>
                        <Typography variant="body2" color="text.secondary" paragraph>
                          {news.description}
                        </Typography>
                        <Typography variant="caption" color="text.secondary">
                          {news.date}
                        </Typography>
                      </CardContent>
                    </NewsCard>
                  </motion.div>
                </Grid>
              ))}
            </Grid>
          </motion.div>
        </Box>
      </NewsSection>

      <AboutSection id="about-section">
        <Box maxWidth="lg" mx="auto">
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8 }}
            viewport={{ once: true }}
          >
            <Typography
              variant="h2"
              component="h2"
              align="center"
              gutterBottom
              sx={{
                fontWeight: 'bold',
                mb: 8,
                color: 'primary.main',
              }}
            >
              TEAM VXM
            </Typography>

            <Grid container spacing={6}>
              {/* Team Member 1 */}
              <Grid item xs={12} md={4}>
                <motion.div
                  initial={{ opacity: 0, y: 20 }}
                  whileInView={{ opacity: 1, y: 0 }}
                  transition={{ duration: 0.5 }}
                  viewport={{ once: true }}
                >
                  <TeamCard elevation={3}>
                    <CardMedia
                      component="img"
                      height="300"
                      image="/path-to-member1-image.jpg"
                      alt="Team Member 1"
                      sx={{ objectFit: 'cover' }}
                    />
                    <CardContent>
                      <Typography variant="h5" gutterBottom align="center">
                        Vishal
                      </Typography>
                      <Typography variant="subtitle1" color="text.secondary" gutterBottom align="center">
                        Full Stack Developer
                      </Typography>
                      <Box sx={{ display: 'flex', justifyContent: 'center', gap: 1, mt: 2 }}>
                        <IconButton color="primary" href="https://linkedin.com/in/member1" target="_blank">
                          <LinkedInIcon />
                        </IconButton>
                        <IconButton color="primary" href="https://github.com/member1" target="_blank">
                          <GitHubIcon />
                        </IconButton>
                        <IconButton color="primary" href="mailto:member1@example.com">
                          <EmailIcon />
                        </IconButton>
                      </Box>
                    </CardContent>
                  </TeamCard>
                </motion.div>
              </Grid>

              {/* Team Member 2 */}
              <Grid item xs={12} md={4}>
                <motion.div
                  initial={{ opacity: 0, y: 20 }}
                  whileInView={{ opacity: 1, y: 0 }}
                  transition={{ duration: 0.5, delay: 0.2 }}
                  viewport={{ once: true }}
                >
                  <TeamCard elevation={3}>
                    <CardMedia
                      component="img"
                      height="300"
                      image="/path-to-member2-image.jpg"
                      alt="Team Member 2"
                      sx={{ objectFit: 'cover' }}
                    />
                    <CardContent>
                      <Typography variant="h5" gutterBottom align="center">
                        Xavier
                      </Typography>
                      <Typography variant="subtitle1" color="text.secondary" gutterBottom align="center">
                        UI/UX Designer
                      </Typography>
                      <Box sx={{ display: 'flex', justifyContent: 'center', gap: 1, mt: 2 }}>
                        <IconButton color="primary" href="https://linkedin.com/in/member2" target="_blank">
                          <LinkedInIcon />
                        </IconButton>
                        <IconButton color="primary" href="https://github.com/member2" target="_blank">
                          <GitHubIcon />
                        </IconButton>
                        <IconButton color="primary" href="mailto:member2@example.com">
                          <EmailIcon />
                        </IconButton>
                      </Box>
                    </CardContent>
                  </TeamCard>
                </motion.div>
              </Grid>

              {/* Team Member 3 */}
              <Grid item xs={12} md={4}>
                <motion.div
                  initial={{ opacity: 0, y: 20 }}
                  whileInView={{ opacity: 1, y: 0 }}
                  transition={{ duration: 0.5, delay: 0.4 }}
                  viewport={{ once: true }}
                >
                  <TeamCard elevation={3}>
                    <CardMedia
                      component="img"
                      height="300"
                      image="/path-to-member3-image.jpg"
                      alt="Team Member 3"
                      sx={{ objectFit: 'cover' }}
                    />
                    <CardContent>
                      <Typography variant="h5" gutterBottom align="center">
                        Michael
                      </Typography>
                      <Typography variant="subtitle1" color="text.secondary" gutterBottom align="center">
                        Backend Developer
                      </Typography>
                      <Box sx={{ display: 'flex', justifyContent: 'center', gap: 1, mt: 2 }}>
                        <IconButton color="primary" href="https://linkedin.com/in/member3" target="_blank">
                          <LinkedInIcon />
                        </IconButton>
                        <IconButton color="primary" href="https://github.com/member3" target="_blank">
                          <GitHubIcon />
                        </IconButton>
                        <IconButton color="primary" href="mailto:member3@example.com">
                          <EmailIcon />
                        </IconButton>
                      </Box>
                    </CardContent>
                  </TeamCard>
                </motion.div>
              </Grid>
            </Grid>

            <ContactCard elevation={3}>
              <Typography
                variant="h3"
                component="h3"
                align="center"
                gutterBottom
                sx={{ color: 'primary.main', mb: 4 }}
              >
                Contact Us
              </Typography>
              <Divider sx={{ mb: 4 }} />
              
              <Grid container spacing={4}>
                <Grid item xs={12} md={6}>
                  <ContactItem>
                    <LocationOnIcon />
                    <Box>
                      <Typography variant="h6" gutterBottom>
                        Our Location
                      </Typography>
                      <Typography variant="body1" color="text.secondary">
                        123 Innovation Street<br />
                        Tech Hub, Digital City<br />
                        IN 400001
                      </Typography>
                    </Box>
                  </ContactItem>
                </Grid>

                <Grid item xs={12} md={6}>
