import React, { useState } from 'react';
import {
  Box,
  Container,
  Grid,
  Paper,
  Typography,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Button,
  Card,
  CardContent,
  LinearProgress,
  IconButton,
  Chip,
} from '@mui/material';
import { styled } from '@mui/material/styles';
import CheckCircleIcon from '@mui/icons-material/CheckCircle';
import CancelIcon from '@mui/icons-material/Cancel';
import TrendingUpIcon from '@mui/icons-material/TrendingUp';
import StorageIcon from '@mui/icons-material/Storage';
import PeopleIcon from '@mui/icons-material/People';
import { motion } from 'framer-motion';

const StyledCard = styled(Card)(({ theme }) => ({
  height: '100%',
  display: 'flex',
  flexDirection: 'column',
  transition: 'transform 0.2s',
  '&:hover': {
    transform: 'translateY(-4px)',
  },
}));

const StatNumber = styled(Typography)(({ theme }) => ({
  fontSize: '2rem',
  fontWeight: 'bold',
  color: theme.palette.primary.main,
}));

const AdminDashboard = () => {
  const [pendingJudgments, setPendingJudgments] = useState([
    {
      id: 1,
      title: 'Smith vs State of California',
      date: '2024-02-15',
      court: 'Supreme Court',
      status: 'pending',
    },
    {
      id: 2,
      title: 'Digital Rights Foundation Case',
      date: '2024-02-14',
      court: 'High Court',
      status: 'pending',
    },
  ]);

  const stats = {
    totalJudgments: 1234,
    activeUsers: 567,
    aiAccuracy: 95,
  };

  const handleApprove = (id) => {
    setPendingJudgments(pendingJudgments.filter((item) => item.id !== id));
  };

  const handleReject = (id) => {
    setPendingJudgments(pendingJudgments.filter((item) => item.id !== id));
  };

  return (
    <Container maxWidth="xl" sx={{ mt: 10, mb: 4 }}>
      <Typography variant="h4" gutterBottom>
        Admin Dashboard
      </Typography>

      <Grid container spacing={3}>
        {/* Stats Cards */}
        <Grid item xs={12} md={4}>
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.3 }}
          >
            <StyledCard>
              <CardContent>
                <Box display="flex" alignItems="center" mb={2}>
                  <StorageIcon color="primary" sx={{ mr: 1 }} />
                  <Typography variant="h6">Total Judgments</Typography>
                </Box>
                <StatNumber>{stats.totalJudgments}</StatNumber>
                <Typography variant="body2" color="text.secondary">
                  Stored in the database
                </Typography>
              </CardContent>
            </StyledCard>
          </motion.div>
        </Grid>

        <Grid item xs={12} md={4}>
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.3, delay: 0.1 }}
          >
            <StyledCard>
              <CardContent>
                <Box display="flex" alignItems="center" mb={2}>
                  <PeopleIcon color="primary" sx={{ mr: 1 }} />
                  <Typography variant="h6">Active Users</Typography>
                </Box>
                <StatNumber>{stats.activeUsers}</StatNumber>
                <Typography variant="body2" color="text.secondary">
                  Users this month
                </Typography>
              </CardContent>
            </StyledCard>
          </motion.div>
        </Grid>

        <Grid item xs={12} md={4}>
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.3, delay: 0.2 }}
          >
            <StyledCard>
              <CardContent>
                <Box display="flex" alignItems="center" mb={2}>
                  <TrendingUpIcon color="primary" sx={{ mr: 1 }} />
                  <Typography variant="h6">AI Model Accuracy</Typography>
                </Box>
                <Box display="flex" alignItems="center">
                  <StatNumber>{stats.aiAccuracy}%</StatNumber>
                </Box>
                <LinearProgress
                  variant="determinate"
                  value={stats.aiAccuracy}
                  sx={{ mt: 1 }}
                />
              </CardContent>
            </StyledCard>
          </motion.div>
        </Grid>

        {/* Pending Judgments Table */}
        <Grid item xs={12}>
          <Paper sx={{ p: 3, mt: 3 }}>
            <Typography variant="h6" gutterBottom>
              Pending Judgments
            </Typography>
            <TableContainer>
              <Table>
                <TableHead>
                  <TableRow>
                    <TableCell>Title</TableCell>
                    <TableCell>Date</TableCell>
                    <TableCell>Court</TableCell>
                    <TableCell>Status</TableCell>
                    <TableCell align="right">Actions</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {pendingJudgments.map((judgment) => (
                    <TableRow key={judgment.id}>
                      <TableCell>{judgment.title}</TableCell>
                      <TableCell>{judgment.date}</TableCell>
                      <TableCell>{judgment.court}</TableCell>
                      <TableCell>
                        <Chip
                          label={judgment.status}
                          color="warning"
                          size="small"
                        />
                      </TableCell>
                      <TableCell align="right">
                        <IconButton
                          color="success"
                          onClick={() => handleApprove(judgment.id)}
                        >
                          <CheckCircleIcon />
                        </IconButton>
                        <IconButton
                          color="error"
                          onClick={() => handleReject(judgment.id)}
                        >
                          <CancelIcon />
                        </IconButton>
                      </TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </TableContainer>
          </Paper>
        </Grid>
      </Grid>
    </Container>
  );
};

export default AdminDashboard;
