import React, { useState, useRef, useEffect } from 'react';
import {
  Box,
  Container,
  Paper,
  Typography,
  TextField,
  IconButton,
  Chip,
  List,
  Divider,
} from '@mui/material';
import { styled } from '@mui/material/styles';
import SendIcon from '@mui/icons-material/Send';
import MicIcon from '@mui/icons-material/Mic';
import { motion } from 'framer-motion';

const ChatContainer = styled(Paper)(({ theme }) => ({
  height: '100vh',
  display: 'flex',
  flexDirection: 'column',
  background: theme.palette.background.default,
  borderRadius: 0,
  overflow: 'hidden',
  boxShadow: 'none',
}));

const MessageList = styled(List)(({ theme }) => ({
  flex: 1,
  overflow: 'auto',
  padding: theme.spacing(2),
  backgroundColor: theme.palette.background.paper,
}));

const InputContainer = styled(Box)(({ theme }) => ({
  padding: theme.spacing(3),
  borderTop: `1px solid ${theme.palette.divider}`,
  backgroundColor: theme.palette.grey[900],
  color: theme.palette.common.white,
}));

const Message = styled(Paper)(({ theme, isUser }) => ({
  padding: theme.spacing(1.5),
  marginBottom: theme.spacing(1),
  maxWidth: '70%',
  alignSelf: isUser ? 'flex-end' : 'flex-start',
  backgroundColor: isUser ? theme.palette.primary.main : theme.palette.grey[100],
  color: isUser ? theme.palette.primary.contrastText : theme.palette.text.primary,
  borderRadius: theme.spacing(2),
}));

const QuickPromptButton = styled(Chip)(({ theme }) => ({
  margin: theme.spacing(0.5),
  cursor: 'pointer',
  '&:hover': {
    backgroundColor: theme.palette.primary.light,
    color: theme.palette.primary.contrastText,
  },
}));

const Chatbot = () => {
  const [messages, setMessages] = useState([
    {
      text: "Hello! I am your AI legal assistant. How can I help you today?",
      isUser: false,
    },
  ]);
  const [input, setInput] = useState('');
  const [isRecording, setIsRecording] = useState(false);
  const messagesEndRef = useRef(null);

  const quickPrompts = [
    'Summarize this case',
    'Find related cases',
    'Explain key legal principles',
  ];

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const handleSend = () => {
    if (input.trim()) {
      setMessages([...messages, { text: input, isUser: true }]);
      // Simulate AI response
      setTimeout(() => {
        setMessages((prev) => [
          ...prev,
          {
            text: "I am processing your request. Here is what I found...",
            isUser: false,
          },
        ]);
      }, 1000);
      setInput('');
    }
  };

  const handleQuickPrompt = (prompt) => {
    setInput(prompt);
  };

  const handleVoiceInput = () => {
    setIsRecording(!isRecording);
    // Implement voice recognition logic here
  };

  return (
    <motion.div
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      transition={{ duration: 0.5 }}
    >
      <Box
        sx={{
          minHeight: '100vh',
          background: 'linear-gradient(135deg, #f5f7fa 0%, #c3cfe2 100%)',
        }}
      >
        <Container maxWidth="lg" sx={{ height: '100vh', p: 0 }}>
          <motion.div
            initial={{ y: 20, opacity: 0 }}
            animate={{ y: 0, opacity: 1 }}
            transition={{ delay: 0.2, duration: 0.5 }}
            style={{ height: '100%' }}
          >
            <ChatContainer>
              <Box p={2} bgcolor="primary.main" color="primary.contrastText">
                <Typography variant="h6">Legal AI Assistant</Typography>
              </Box>

              <MessageList>
                {messages.map((message, index) => (
                  <motion.div
                    key={index}
                    initial={{ opacity: 0, y: 20 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ duration: 0.3 }}
                  >
                    <Message isUser={message.isUser}>
                      <Typography>{message.text}</Typography>
                    </Message>
                  </motion.div>
                ))}
                <div ref={messagesEndRef} />
              </MessageList>

              <Divider />

              <Box p={2} sx={{ bgcolor: 'grey.200' }}>
                <Typography variant="subtitle2" gutterBottom>
                  Quick Prompts:
                </Typography>
                {quickPrompts.map((prompt) => (
                  <QuickPromptButton
                    key={prompt}
                    label={prompt}
                    onClick={() => handleQuickPrompt(prompt)}
                    color="primary"
                    variant="outlined"
                  />
                ))}
              </Box>

              <InputContainer>
                <Box display="flex" alignItems="center" gap={1}>
                  <TextField
                    fullWidth
                    variant="outlined"
                    placeholder="Type your message..."
                    value={input}
                    onChange={(e) => setInput(e.target.value)}
                    onKeyPress={(e) => e.key === 'Enter' && handleSend()}
                    sx={{
                      '& .MuiOutlinedInput-root': {
                        backgroundColor: 'white',
                      }
                    }}
                  />
                  <IconButton
                    color={isRecording ? 'error' : 'default'}
                    onClick={handleVoiceInput}
                    sx={{ color: 'white' }}
                  >
                    <MicIcon />
                  </IconButton>
                  <IconButton 
                    color="default"
                    onClick={handleSend}
                    sx={{ color: 'white' }}
                  >
                    <SendIcon />
                  </IconButton>
                </Box>
              </InputContainer>
            </ChatContainer>
          </motion.div>
        </Container>
      </Box>
    </motion.div>
  );
};

export default Chatbot;
