import React from 'react';
import { Box, Container, Typography, Grid, Card, CardMedia, CardContent } from '@mui/material';
import { styled } from '@mui/material/styles';
import { motion } from 'framer-motion';

const NewsCard = styled(Card)(({ theme }) => ({
  height: '100%',
  display: 'flex',
  flexDirection: 'column',
  transition: 'transform 0.3s ease-in-out',
  '&:hover': {
    transform: 'translateY(-8px)',
  },
}));

const newsItems = [
  {
    title: "Major Cybercrime Ring Busted",
    description: "International law enforcement agencies collaborate to dismantle a global cybercrime network responsible for millions in fraud.",
    image: "https://images.unsplash.com/photo-1550751827-4bd374c3f58b?auto=format&fit=crop&w=800&q=80",
    date: "March 15, 2024"
  },
  {
    title: "Supreme Court's Landmark Digital Privacy Ruling",
    description: "Supreme Court establishes new precedent for digital privacy rights in the age of social media and data collection.",
    image: "https://images.unsplash.com/photo-1589829545856-d10d557cf95f?auto=format&fit=crop&w=800&q=80",
    date: "March 12, 2024"
  },
  {
    title: "AI in Criminal Justice System",
    description: "How artificial intelligence is helping solve cold cases and improve criminal investigations across the country.",
    image: "https://images.unsplash.com/photo-1507925921958-8a62f3d1a50d?auto=format&fit=crop&w=800&q=80",
    date: "March 10, 2024"
  }
];

function Home() {
  return (
    <Box sx={{ minHeight: '100vh', pt: 10, pb: 4 }}>
      <Container maxWidth="lg">
        {/* Hero Section */}
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5 }}
        >
          <Box textAlign="center" mb={8}>
            <Typography
              variant="h2"
              component="h1"
              gutterBottom
              sx={{
                fontWeight: 'bold',
                background: 'linear-gradient(45deg, #2196F3 30%, #21CBF3 90%)',
                WebkitBackgroundClip: 'text',
                WebkitTextFillColor: 'transparent',
              }}
            >
              Search Legal Cases
            </Typography>
            <Typography variant="h5" color="text.secondary" paragraph>
              Access thousands of court cases and legal documents instantly
            </Typography>
          </Box>
        </motion.div>

        {/* Popular News Section */}
        <Box mb={6}>
          <Typography variant="h4" component="h2" gutterBottom sx={{ mb: 4 }}>
            Popular News
          </Typography>
          <Grid container spacing={4}>
            {newsItems.map((item, index) => (
              <Grid item xs={12} md={4} key={index} sx={{ display: 'flex' }}>
                <motion.div
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ duration: 0.5, delay: index * 0.1 }}
                  style={{ width: '100%' }}
                >
                  <NewsCard>
                    <CardMedia
                      component="img"
                      height="200"
                      image={item.image}
                      alt={item.title}
                      sx={{
                        filter: 'brightness(0.9)',
                        transition: 'filter 0.3s ease',
                        '&:hover': {
                          filter: 'brightness(1)',
                        },
                      }}
                    />
                    <CardContent sx={{ flexGrow: 1, display: 'flex', flexDirection: 'column' }}>
                      <Box sx={{ flexGrow: 1 }}>
                        <Typography gutterBottom variant="h6" component="h3">
                          {item.title}
                        </Typography>
                        <Typography variant="body2" color="text.secondary" paragraph>
                          {item.description}
                        </Typography>
                      </Box>
                      <Typography variant="caption" color="text.secondary">
                        {item.date}
                      </Typography>
                    </CardContent>
                  </NewsCard>
                </motion.div>
              </Grid>
            ))}
          </Grid>
        </Box>
      </Container>
    </Box>
  );
}

export default Home;
