import React, { useState } from 'react';
import {
  Box,
  Container,
  Grid,
  Paper,
  Typography,
  TextField,
  Select,
  MenuItem,
  FormControl,
  InputLabel,
  Button,
  Card,
  CardContent,
  IconButton,
  useMediaQuery,
  useTheme,
} from '@mui/material';
import { styled } from '@mui/material/styles';
import FilterListIcon from '@mui/icons-material/FilterList';
import CloseIcon from '@mui/icons-material/Close';
import { motion, AnimatePresence } from 'framer-motion';

const FilterPanel = styled(Paper)(({ theme }) => ({
  padding: theme.spacing(2),
  height: '100%',
  position: 'sticky',
  top: theme.spacing(10),
}));

const SearchResultCard = styled(Card)(({ theme }) => ({
  marginBottom: theme.spacing(2),
  transition: 'transform 0.2s ease-in-out',
  '&:hover': {
    transform: 'translateY(-4px)',
  },
}));

const Search = () => {
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('md'));
  const [showFilters, setShowFilters] = useState(!isMobile);
  const [filters, setFilters] = useState({
    court: '',
    year: '',
    caseType: '',
    judge: '',
  });

  const mockResults = [
    {
      id: 1,
      title: 'Smith vs State of California',
      date: '2023-12-15',
      court: 'Supreme Court',
      summary: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat.',
    },
    // Add more mock results as needed
  ];

  const handleFilterChange = (field) => (event) => {
    setFilters({ ...filters, [field]: event.target.value });
  };

  const FilterSection = () => (
    <FilterPanel elevation={3}>
      {isMobile && (
        <Box display="flex" justifyContent="flex-end">
          <IconButton onClick={() => setShowFilters(false)}>
            <CloseIcon />
          </IconButton>
        </Box>
      )}
      <Typography variant="h6" gutterBottom>
        Filters
      </Typography>
      <Box display="flex" flexDirection="column" gap={2}>
        <FormControl fullWidth>
          <InputLabel>Court</InputLabel>
          <Select
            value={filters.court}
            label="Court"
            onChange={handleFilterChange('court')}
          >
            <MenuItem value="supreme">Supreme Court</MenuItem>
            <MenuItem value="high">High Court</MenuItem>
            <MenuItem value="district">District Court</MenuItem>
          </Select>
        </FormControl>

        <FormControl fullWidth>
          <InputLabel>Year</InputLabel>
          <Select
            value={filters.year}
            label="Year"
            onChange={handleFilterChange('year')}
          >
            <MenuItem value="2024">2024</MenuItem>
            <MenuItem value="2023">2023</MenuItem>
            <MenuItem value="2022">2022</MenuItem>
          </Select>
        </FormControl>

        <FormControl fullWidth>
          <InputLabel>Case Type</InputLabel>
          <Select
            value={filters.caseType}
            label="Case Type"
            onChange={handleFilterChange('caseType')}
          >
            <MenuItem value="civil">Civil</MenuItem>
            <MenuItem value="criminal">Criminal</MenuItem>
            <MenuItem value="corporate">Corporate</MenuItem>
          </Select>
        </FormControl>

        <FormControl fullWidth>
          <InputLabel>Judge</InputLabel>
          <Select
            value={filters.judge}
            label="Judge"
            onChange={handleFilterChange('judge')}
          >
            <MenuItem value="judge1">Judge 1</MenuItem>
            <MenuItem value="judge2">Judge 2</MenuItem>
            <MenuItem value="judge3">Judge 3</MenuItem>
          </Select>
        </FormControl>
      </Box>
    </FilterPanel>
  );

  return (
    <Container maxWidth="xl" sx={{ mt: 10, mb: 4 }}>
      <Grid container spacing={3}>
        {isMobile && (
          <Grid item xs={12}>
            <Button
              startIcon={<FilterListIcon />}
              onClick={() => setShowFilters(true)}
              variant="outlined"
              fullWidth
            >
              Show Filters
            </Button>
          </Grid>
        )}

        <AnimatePresence>
          {(showFilters || !isMobile) && (
            <Grid
              item
              xs={12}
              md={3}
              component={motion.div}
              initial={{ x: -100, opacity: 0 }}
              animate={{ x: 0, opacity: 1 }}
              exit={{ x: -100, opacity: 0 }}
            >
              <FilterSection />
            </Grid>
          )}
        </AnimatePresence>

        <Grid item xs={12} md={showFilters ? 9 : 12}>
          <Box mb={4}>
            <TextField
              fullWidth
              variant="outlined"
              placeholder="Search for cases, judgments, or legal topics..."
              sx={{ mb: 2 }}
            />
          </Box>

          <Box>
            {mockResults.map((result) => (
              <SearchResultCard key={result.id}>
                <CardContent>
                  <Typography variant="h6" gutterBottom>
                    {result.title}
                  </Typography>
                  <Typography variant="subtitle2" color="text.secondary" gutterBottom>
                    {result.court} â€¢ {result.date}
                  </Typography>
                  <Typography variant="body2" paragraph>
                    {result.summary}
                  </Typography>
                  <Button variant="contained" color="primary">
                    Read More
                  </Button>
                </CardContent>
              </SearchResultCard>
            ))}
          </Box>
        </Grid>
      </Grid>
    </Container>
  );
};

export default Search;
